import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import 'react-dropdown/style.css';


const Record = (props) => (
    
    
    <tr>
        <button className="btn btn-link" onClick={() => {
            props.sortRecords();
        }}>sort</button>
        <td>{props.record.name}</td>
        <td>
            <Link className="btn btn-link" to={`/edit/${props.record._id}/industriesDB`}>Edit</Link> |
            <button className="btn btn-link"
                onClick={() => {
                    props.deleteRecord(props.record._id, "industriesDB");
                }}
            >
                Delete
            </button>
        </td>
    </tr>
    
);

const IotRecord = (props) => (

    <tr>
        <td>{props.iotRecord.DeviceName}</td>
        <td>{props.iotRecord.wAdditionTime}</td>
        <td>{props.iotRecord.fee}</td>
        <td>{props.iotRecord.Industry}</td>
        <td>
            <Link className="btn btn-link" to={`/edit/${props.iotRecord._id}/iotDB`}>Edit</Link> |
            <button className="btn btn-link"
                onClick={() => {
                    props.deleteRecord(props.iotRecord._id, "iotDB");
                }}
            >
                Delete
            </button>
        </td>
    </tr>

);

const sortTableData = (array, sortBy) => {
    return array.sort((a, b) => {
        if (a[sortBy] < b[sortBy]) return -1
        if (a[sortBy] > b[sortBy]) return 1
        return 0
    })
}

export default function RecordList() {
    const [records, setRecords] = useState([]);
    const [iotRecords, setIotRecords] = useState([]);

    const [search, setSearch] = React.useState('');
    const handleSearch = (event) => {
        setSearch(event.target.value);
    };

    // This method fetches the records from the database.
    useEffect(() => {
        async function getRecords(table) {
            const response = await fetch(`http://localhost:5050/record/table`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ table: table }),

            });

            if (!response.ok) {
                const message = `An error occurred: ${response.statusText}`;
                window.alert(message);
                return;
            }
            if(table ==  "iotDB"){
                const iotRecords = await response.json();
                setIotRecords(iotRecords);
            }
            else if (table == "industriesDB"){
                const records = await response.json();
                setRecords(records);
            } 
            
        }
        getRecords("iotDB");
        getRecords("industriesDB");



        

        return;
    }, [records.length]);

    async function test(){
        window.alert(JSON.stringify(records));
        const sorted = sortTableData(records, "name");
        //window.alert(sortTableData(records, "name"));
        window.alert(JSON.stringify(sorted));
        //let srecords = sortTableData(records, "name");
        setRecords(sorted);
        setRecords(sorted);

        //records.setState({ key: Math.random() });
        
    }

    // This method will delete a record
    async function deleteRecord(id, table) {
        await fetch(`http://localhost:5050/record/delete`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ table: table, id: id }),
        });

        
        if(table == "industriesDB"){
            const newRecords = records.filter((el) => el._id !== id);
            setRecords(newRecords);

        }
        else if(table == "iotDB"){
            const newRecords = iotRecords.filter((el) => el._id !== id);
            setIotRecords(newRecords);

        }
    }

    // This method will map out the records on the table
    function recordList() {
        return records.map((record) => {
            return (
                <Record
                    record={record}
                    deleteRecord={() => deleteRecord(record._id, "industriesDB")}
                    sortRecords={() => test()}
                    key={record._id}
                />
            );
        });
    }

    function iotRecordList() {
        return iotRecords.map((iotRecord) => {
            return (
                <IotRecord
                    iotRecord={iotRecord}
                    deleteRecord={() => deleteRecord(iotRecord._id, "iotDB")}
                    key={iotRecord._id}
                />
            );
        });
    }
    

        // This following section will display the table with the records of individuals.
    return (
        
        <div>
            <h3>Industries List</h3>
            <label htmlFor="search">
                Search by Task:
                <input id="search" type="text" onChange={handleSearch} />

            </label>
            <table data={records} className="table table-striped" style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>{recordList()}</tbody>
            </table>
            <h3>IoT List</h3>
            <table className="table table-striped" style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Device Name</th>
                        <th>Addition Time</th>
                        <th>Fee</th>
                        <th>Industry</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>{iotRecordList()}</tbody>
            </table>
        </div>
    );
}
