import React, { useEffect, useState, Component } from "react";

export let filteredRecords = {};

function testSort(e) {
    async function getRecords(table) {
        const response = await fetch(`http://localhost:5050/record/table`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ table: table }),

        });

        if (!response.ok) {
            const message = `An error occurred: ${response.statusText}`;
            window.alert(message);
            return;
        }
        if (table == "iotDB") {
            let iotRecords = await response.json();
        }
        else if (table == "industriesDB") {
            let records = await response.json();
        }

    }

}


export class Sort extends Component {
    render() {
        return (
        <select onChange={testSort}> 
            <option>Silicon Manufacturing</option>
            <option>Gaming</option>
        </select>)

    }


}
