import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Dropdown from "react-dropdown"
import 'react-dropdown/style.css';


// const options = [
//     'Industries', 'IoT'
// ];
// const defaultOption = options[0];



const Record = (props) => (
    
    <tr>
        <td>{props.record.name}</td>
        <td>{props.record.position}</td>
        <td>{props.record.level}</td>
        <td>
            <Link className="btn btn-link" to={`/edit/${props.record._id}`}>Edit</Link> |
            <button className="btn btn-link"
                onClick={() => {
                    props.deleteRecord(props.record._id);
                }}
            >
                Delete
            </button>
        </td>
    </tr>
    
);

const IotRecord = (props) => (

    <tr>
        <td>{props.record.DeviceName}</td>
        <td>{props.record.wAdditionTime}</td>
        <td>{props.record.fee}</td>
        <td>{props.record.Industry}</td>
        <td>
            <Link className="btn btn-link" to={`/edit/${props.record._id}`}>Edit</Link> |
            <button className="btn btn-link"
                onClick={() => {
                    props.deleteRecord(props.record._id);
                }}
            >
                Delete
            </button>
        </td>
    </tr>

);

export default function RecordList(e) {
    const [records, setRecords] = useState([]);
    const [iotRecords, setIotRecords] = useState([]);


    

    // This method fetches the records from the database.
    useEffect(() => {
        async function getRecords() {
            const response = await fetch(`http://localhost:5050/record/table`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ table: "industriesDB" }),

            });

            if (!response.ok) {
                const message = `An error occurred: ${response.statusText}`;
                window.alert(message);
                return;
            }

            const records = await response.json();
            setRecords(records);
        }

        getRecords();

        return;
    }, [records.length]);

    // This method will delete a record
    async function deleteRecord(id) {
        await fetch(`http://localhost:5050/record/${id}`, {
            method: "DELETE"
        });

        const newRecords = records.filter((el) => el._id !== id);
        setRecords(newRecords);
    }

    // This method will map out the records on the table
    function recordList() {
        //window.alert("message");
        return records.map((record) => {
            return (
                <Record
                    record={record}
                    deleteRecord={() => deleteRecord(record._id)}
                    key={record._id}
                />
            );
        });
    }

    async function handleSelect(e){
        window.alert(e.target.value);
        const response = await fetch(`http://localhost:5050/record/table`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({table : "iotDB"}),
            
        });
        const newnewrecords = await response.json();
        setRecords(newnewrecords);
        window.alert(newnewrecords);
        hello();

    }

    function hello(){
        return (<h3>Recorafdsafdd List</h3>);
    }
    

    const [options, setOptions] = useState();
    // This following section will display the table with the records of individuals.
    return (
        
        <div>
            <h3>Record List</h3>
            <select value={options} onChange={handleSelect}>
                <option>Industries</option>
                <option>Iot</option>
            </select>
            <table className="table table-striped" style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Position</th>
                        <th>Level</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>{recordList()}</tbody>
            </table>
        </div>
    );
}

