import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import 'react-dropdown/style.css';


// const options = [
//     'Industries', 'IoT'
// ];
// const defaultOption = options[0];



const Record = (props) => (
    
    <tr>
        <td>{props.record.name}</td>
        <td>{props.record.wAdditionTime}</td>
        <td>{props.record.level}</td>
        <td>
            <Link className="btn btn-link" to={`/edit/${props.record._id}`}>Edit</Link> |
            <button className="btn btn-link"
                onClick={() => {
                    props.deleteRecord(props.record._id);
                }}
            >
                Delete
            </button>
        </td>
    </tr>
    
);

const IotRecord = (props) => (

    <tr>
        <td>{props.iotRecord.DeviceName}</td>
        <td>{props.iotRecord.wAdditionTime}</td>
        <td>{props.iotRecord.fee}</td>
        <td>{props.iotRecord.Industry}</td>
        <td>
            <Link className="btn btn-link" to={`/edit/${props.iotRecord._id}`}>Edit</Link> |
            <button className="btn btn-link"
                onClick={() => {
                    props.deleteRecord(props.iotRecord._id);
                }}
            >
                Delete
            </button>
        </td>
    </tr>

);

export default function RecordList() {
    const [records, setRecords] = useState([]);
    const [iotRecords, setIotRecords] = useState([]);

    // This method fetches the records from the database.
    useEffect(() => {
        async function getRecords(table) {
            const response = await fetch(`http://localhost:5050/record/table`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ table: table }),

            });

            if (!response.ok) {
                const message = `An error occurred: ${response.statusText}`;
                window.alert(message);
                return;
            }
            
            if(table ==  "iotDB"){
                const iotRecords = await response.json();
                setRecords(iotRecords);
            }
            else if (table == "industriesDb"){
                const records = await response.json();
                setRecords(records);
            } 

            //window.alert(records)
            
        }
        getRecords("iotDB");
        getRecords("industriesDB");
        
        //window.alert(temp)

        return;
    }, [records.length]);

    // This method will delete a record
    async function deleteRecord(id) {
        await fetch(`http://localhost:5050/record/${id}`, {
            method: "DELETE"
        });

        const newRecords = records.filter((el) => el._id !== id);
        setRecords(newRecords);
    }

    // This method will map out the records on the table
    function recordList() {
        return records.map((record) => {
            return (
                <Record
                    record={record}
                    deleteRecord={() => deleteRecord(record._id)}
                    key={record._id}
                />
            );
        });
    }

    function iotRecordList() {
        //window.alert("message");
        return records.map((record) => {
            return (
                <IotRecord
                    iotRecord={record}
                    deleteRecord={() => deleteRecord(record._id)}
                    key={record._id}
                />
            );
        });
    }

        // This following section will display the table with the records of individuals.
    return (
        
        <div>
            <h3>Industries List</h3>
            <table className="table table-striped" style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Position</th>
                        <th>Level</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>{recordList()}</tbody>
            </table>
            <h3>IoT List</h3>
            <table className="table table-striped" style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Device Name</th>
                        <th>Addition Time</th>
                        <th>Fee</th>
                        <th>Industry</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>{iotRecordList()}</tbody>
            </table>
        </div>
    );
}

