import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";

export default function Edit() {
    const [industryForm, setIndustryForm] = useState({
        name: "",
        records: [],
    });
    const [iotForm, setIotForm] = useState({
        DeviceName: "",
        wAdditionTime: "",
        fee: "",
        Industry: "",
        records: [],
    });
    const params = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchData() {
            const id = params.id.toString();
            //const responses = await fetch(`http://localhost:5050/record/${params.id.toString()}`);

            const response = await fetch(`http://localhost:5050/record/tableid`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ table: params.table.toString(), id: params.id.toString() }),

            });

            if (!response.ok) {
                const message = `An error has occurred: ${response.statusText}`;
                window.alert(message);
                return;
            }

            const record = await response.json();
            if (!record) {
                window.alert(`Record with id ${id} not found`);
                navigate("/");
                return;
            }

            if (params.table.toString() == "industriesDB"){
                setIndustryForm(record);
            }
            else if (params.table.toString() == "iotDB"){
                setIotForm(record);
            }
            //setForm(record);
        }

        fetchData();

        return;
    }, [params.id, navigate]);

    // These methods will update the state properties.
    function updateForm(value, table) {
        if(table == "industriesDB"){
            return setIndustryForm((prev) => {
                return { ...prev, ...value };
            });
        }
        else if (table == "iotDB") {
            return setIotForm((prev) => {
                return { ...prev, ...value };
            });
        }

    }

    async function onSubmit(e, table) {
        e.preventDefault();
        const editedRecord = {};
        if(table == "industriesDB"){
            editedRecord = {
                table: "industriesDB",
                name: industryForm.name,
            };
        }
        else if (table == "iotDB"){
            editedRecord = {
                table: "iotDB",
                DeviceName: iotForm.DeviceName,
                wAdditionTime: iotForm.wAdditionTime,
                fee: iotForm.fee,
                Industry: iotForm.Industry
            };
        }
        
        

        // This will send a post request to update the data in the database.
        await fetch(`http://localhost:5050/record/edit`, {
            method: "POST",
            body: JSON.stringify(editedRecord),
            headers: {
                'Content-Type': 'application/json'
            },
        });

        navigate("/");
    }

    if (params.table.toString() == "industriesDB") {
        return (
            <div>
                <h3>Update Industries Record</h3>
                <form onSubmit={onSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Name: </label>
                        <input
                            type="text"
                            className="form-control"
                            id="name"
                            value={industryForm.name}
                            onChange={(e) => updateForm({ name: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="submit"
                            value="Update Record"
                            className="btn btn-primary"
                        />
                    </div>
                </form>
            </div>
        );
    }
    else if (params.table.toString() == "iotDB"){
        // This following section will display the form that takes input from the user to update the data.
        return (
            <div>
                <h3>Update IoT Record</h3>
                <form onSubmit={onSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Name: </label>
                        <input
                            type="text"
                            className="form-control"
                            id="name"
                            value={iotForm.DeviceName}
                            
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="position">Addition Time: </label>
                        <input
                            type="text"
                            className="form-control"
                            id="wAdditionTime"
                            value={iotForm.wAdditionTime}
                            onChange={(e) => updateForm({ wAdditionTime: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="position">Fee: </label>
                        <input
                            type="text"
                            className="form-control"
                            id="fee"
                            value={iotForm.fee}
                            onChange={(e) => updateForm({ fee: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="position">Industry: </label>
                        <input
                            type="text"
                            className="form-control"
                            id="industry"
                            value={iotForm.Industry}
                            onChange={(e) => updateForm({ Industry: e.target.value })}
                        />
                    </div>


                    <div className="form-group">
                        <input
                            type="submit"
                            value="Update Record"
                            className="btn btn-primary"
                        />
                    </div>
                </form>
            </div>
        );
    }



}
