import React, { useEffect, useState, Component } from "react";
import { getAllRecords } from "../api/records";

export let filteredRecords = {};
export let listOfIndustries = [];
export let selectedIndustry = "";

async function testSort(e) {
    selectedIndustry = e.target.value;
    const [searchForm, setSearchForm] = useState({
        table: "iotDB",
        value: e.target.value,
    });
    const response = await fetch(`http://localhost:5050/record/search`, {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
        },
        body : JSON.stringify({ table: "iotDB", field: "Industry", value: e.target.value })

    });

    if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
    }
    filteredRecords = await response.json();

    window.alert(selectedIndustry);


}

async function getListOfIndustries(){
    
    let records = await getAllRecords("industriesDB");
    listOfIndustries = records.map(a => a.name);


}
getListOfIndustries();


export class Sort extends Component {
    render() {
        return (
        <select name="industrySelect" onChange={testSort}> 
                {listOfIndustries.map(val => <option value={val}>{val}</option>)}
        </select>)

    }


}
